mapper.select:
SELECT C.CI_NUM, C.CI_NAME, C.CI_LOGOFILE, L.M_NUM, C.CI_TITLE, C.CI_IMGFILE 
FROM CIRCLE C, LIKECIRCLE L 
WHERE C.CI_NUM=L.CI_NUM AND L.M_NUM=#{m_num}

SELECT COUNT(*)  
FROM LIKECIRCLE 
WHERE CI_NUM=#{ci_num}

SELECT COUNT(*)  
FROM LIKECIRCLE 
WHERE M_NUM=#{m_num} AND CI_NUM=#{ci_num}
