mapper.insert:

   insert into member
   (
   m_num, m_id, m_pwd, m_name, m_phone, m_email,m_address,m_nickname,m_birth,m_univnum,m_dept
   )
   values
   
   (member_seq.nextval,#{m_id},#{m_pwd},#{m_name},#{m_phone},#{m_email},#{m_address},#{m_nickname},#{m_birth},#{m_univnum}
   ,#{m_dept}
 )
