mapper.select:
select * from
(
	select b.*,rownum rnum from 
    ( 
       select * from SUBJECTRATE where s_num=#{s_num}
        order by sr_num desc
    ) b
) 
 where rnum>=#{startRow} and rnum<=#{endRow}

select * from
(
	select b.*,rownum rnum from 
    ( 
       select * from SUBJECTRATE where m_num=#{m_num}
        order by sr_num desc
    ) b
) 
 where rnum>=#{startRow} and rnum<=#{endRow}

mapper.update:
update SUBJECTRATE 
set sr_content=#{sr_content}, sr_recommend=#{sr_recommend}
where sr_num=#{sr_num}
